	function log(address p0, address p1, address p2, bool p3) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(address,address,address,bool)", p0, p1, p2, p3));
	}

	function log(address p0, address p1, address p2, address p3) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(address,address,address,address)", p0, p1, p2, p3));
	}

}

contracts/WriteBatchIndexes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract WriteBatchIndexes {
    uint256[] tokenIdsUint256;
    uint16[] tokenIdsUint16;
    bytes tokenIdsBytes2;
    bytes tokenIdsBytes32;
    uint256 constant COLLECTION_SIZE = 10_000;
    bool[COLLECTION_SIZE] tokenIdsBool;

    function pushUsingMemory(uint256[] memory base, uint256[] memory input)
        public
        pure
        returns (uint256[] memory)
    {
        uint256[] memory _tmp = new uint256[](base.length + input.length);
        for (uint256 i = 0; i < base.length; i++) {
            _tmp[i] = base[i];
        }
        for (uint256 i = 0; i < input.length; i++) {
            _tmp[base.length + i] = input[i];
        }
        return _tmp;
    }

    function pushUsingMemory(uint16[] memory base, uint16[] memory input)
        public
        pure
        returns (uint16[] memory)
    {
        uint16[] memory _tmp = new uint16[](base.length + input.length);
        for (uint256 i = 0; i < base.length; i++) {
            _tmp[i] = base[i];
        }
        for (uint256 i = 0; i < input.length; i++) {
            _tmp[base.length + i] = input[i];
        }
        return _tmp;
    }

    function loopPushUint256(uint256[] memory input) public virtual {
        for (uint256 i = 0; i < input.length; i++) {
            tokenIdsUint256.push(input[i]);
        }
    }

    function loopPushUint16(uint16[] memory input) public virtual {
        for (uint256 i = 0; i < input.length; i++) {
            tokenIdsUint16.push(input[i]);
        }
    }

    function loopPushMemoryUint256(uint256[] memory input) public virtual {
        tokenIdsUint256 = pushUsingMemory(tokenIdsUint256, input);
    }

    function loopPushMemoryUint16(uint16[] memory input) public virtual {
        tokenIdsUint16 = pushUsingMemory(tokenIdsUint16, input);
    }

    function concatBytes2(bytes memory input) public virtual {
        tokenIdsBytes2 = bytes.concat(tokenIdsBytes2, input);
    }

    function abiEncodeBytes2(bytes memory input) public virtual {
        tokenIdsBytes2 = abi.encodePacked(tokenIdsBytes2, input);
    }

    function concatBytes32(bytes memory input) public virtual {
        tokenIdsBytes32 = bytes.concat(tokenIdsBytes32, input);
    }

    function abiEncodeBytes32(bytes memory input) public virtual {
        tokenIdsBytes32 = abi.encodePacked(tokenIdsBytes32, input);
    }

    function loopConcatBytes32(bytes32[] memory input) public virtual {
        for (uint256 i = 0; i < input.length; i++) {
            tokenIdsBytes32 = bytes.concat(tokenIdsBytes32, input[i]);
        }
    }

    function loopConcatBytes2(bytes2[] memory input) public virtual {
        for (uint256 i = 0; i < input.length; i++) {
            tokenIdsBytes2 = bytes.concat(tokenIdsBytes2, input[i]);
        }
    }

    function flipBool(uint256[] memory input) public virtual {
        for (uint256 i = 0; i < input.length; i++) {
            tokenIdsBool[input[i]] = !tokenIdsBool[input[i]];
        }
    }
}

